<?php

namespace Ens\JobeetBundle\Entity;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Tools\Pagination\Paginator;

/**
 * JobRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class JobRepository extends EntityRepository
{
    /**
     * Return query result containing active jobs
     * If a category_id is present, only return active
     * jobs in that category.
     */
    public function getActiveJobs($category_id = null, $max = null, $page = null)
    {
        $querybuilder = $this->createQueryBuilder('j')
            ->where('j.expires_at > :date')
            ->setParameter('date', date('Y-m-d H:i:s', time()))
            ->andWhere('j.isActivated = :activated')
            ->setParameter('activated', 1)
            ->orderBy('j.expires_at', 'DESC');

        if($max)
        {
            $querybuilder->setMaxResults($max);
        }

        if($category_id)
        {
            $querybuilder->andWhere('j.category = :category_id')
                ->setParameter('category_id', $category_id);
        }

        $query = $querybuilder->getQuery();

        if($page)
        {
            $query->setFirstResult( ($page-1) * $max );
        }

        return new Paginator($query);
        //return $query->getResult();
    }

    /**
     * Return single active job for job_id.
     * If the job is not active, then error.
     */
    public function getActiveJob($id)
    {
        $query = $this->createQueryBuilder('j')
            ->where('j.id = :id')
            ->setParameter('id', $id)
            ->andWhere('j.expires_at > :date')
            ->setParameter('date', date('Y-m-d H:i:s', time()))
            ->andWhere('j.isActivated = :activated')
            ->setParameter('activated', 1)
            ->setMaxResults(1)
            ->getQuery();

        try {
            $job = $query->getSingleResult();
        } catch (\Doctrine\ORM\NoResultException $e) {
            $job = null;
        } 

        return $job;
    }

    public function countActiveJobs($category_id = null)
    {
        $querybuilder = $this->createQueryBuilder('j')
            ->select('count(j.id)')
            ->where('j.expires_at > :date')
            ->setParameter('date', date('Y-m-d H:i:s', time()))
            ->andWhere('j.isActivated = :activated')
            ->setParameter('activated', 1);

        if($category_id)
        {
            $querybuilder->andWhere('j.category = :category_id')
                ->setParameter('category_id', $category_id);
        }

        $query = $querybuilder->getQuery();

        return $query->getSingleScalarResult();
    }
}
